mapper.update:
UPDATE yjj_product
SET SaleTotalCount = SaleTotalCount + #{count}
WHERE
Id = #{id}
AND SaleTotalCount + #{count} >= 0

mapper.select:
SELECT su.campaign_image as campaignImage, p.SaleTotalCount + IFNULL(sp.sales_volume,0) as salesVolume, p.`Name` as `name`, p.SaleTotalCount as saleTotalCount, p.minLadderPrice as minLadderPrice, p.BrandId as brandId, p.id as id, p.mainImg as mainImg FROM yjj_product p LEFT JOIN yjj_sales_volume_product sp ON p.Id = sp.product_id LEFT JOIN supplier_user su ON su.brand_id = p.BrandId WHERE 1=1
and p.BrandId in (
#{id}
)

SELECT su.campaign_image as campaignImage, p.SaleTotalCount + IFNULL(sp.sales_volume,0) as salesVolume, p.`Name` as `name`, p.SaleTotalCount as saleTotalCount, p.minLadderPrice as minLadderPrice, p.BrandId as brandId, p.id as id, p.mainImg as mainImg FROM yjj_product p LEFT JOIN yjj_sales_volume_product sp ON p.Id = sp.product_id LEFT JOIN supplier_user su ON su.brand_id = p.BrandId WHERE 1=1
and p.id in (
#{id}
)
AND p.BrandId = #{brandId}
AND state = #{state,jdbcType=TINYINT}
AND delState = #{delState,jdbcType=TINYINT}

SELECT su.campaign_image as campaignImage, p.SaleTotalCount + IFNULL(sp.sales_volume,0) as salesVolume, p.`Name` as `name`, p.SaleTotalCount as saleTotalCount, p.minLadderPrice as minLadderPrice, p.BrandId as brandId, p.id as id, p.mainImg as mainImg FROM yjj_product p LEFT JOIN yjj_sales_volume_product sp ON p.Id = sp.product_id LEFT JOIN supplier_user su ON su.brand_id = p.BrandId WHERE 1=1
and p.id IN (
#{id}
)
AND ( p.`Name` like concat('%',#{keyword},'%') OR p.oneCategoryName LIKE concat('%',#{keyword},'%') OR p.twoCategoryName LIKE concat('%',#{keyword},'%') OR p.threeCategoryName LIKE concat('%',#{keyword},'%') )
AND p.BrandId = #{brandId}
